  methods: {
    getstartYear(time) {
      // 原始日期字符串

      // 使用正则表达式提取年、月、日
      const match = time.match(/(\d{4})年(\d{1,2})月(\d{1,2})日/);
      const year = parseInt(match[1]);
      const month = parseInt(match[2]);
      const day = parseInt(match[3]);

      // 加减三个月
      const newMonth = month + 3;
      const newYear = year + Math.floor(newMonth / 12);
      const newMonthWithinYear = newMonth % 12;

      // 计算新日期
      const newDay = day; // 保持日不变
      const newDate = new Date(newYear, newMonthWithinYear - 1, newDay);

      // 格式化新日期
      const newYearString = newDate.getFullYear().toString();
      const newMonthString = (newDate.getMonth() + 1).toString().padStart(2, "0");
      const newDayString = newDate.getDate().toString().padStart(2, "0");

      // 输出结果
      const newTimeString = newYearString + newMonthString + newDayString;
      console.log("加三个月后的日期:", newTimeString);
      return newTimeString;
    },
    getEndYear(time) {
      // 原始日期字符串

      // 使用正则表达式提取年、月、日
      const match = time.match(/(\d{4})年(\d{1,2})月(\d{1,2})日/);
      const year = parseInt(match[1]);
      const month = parseInt(match[2]);
      const day = parseInt(match[3]);

      // 减去三个月
      const newMonth = month - 3;
      let newYear = year;
      let newMonthWithinYear = newMonth;

      // 处理月份借位
      if (newMonthWithinYear <= 0) {
        newYear -= 1;
        newMonthWithinYear += 12;
      }

      // 计算新日期
      const newDay = day; // 保持日不变
      const newDate = new Date(newYear, newMonthWithinYear - 1, newDay);

      // 格式化新日期
      const newYearString = newDate.getFullYear().toString();
      const newMonthString = (newDate.getMonth() + 1).toString().padStart(2, "0");
      const newDayString = newDate.getDate().toString().padStart(2, "0");

      // 输出结果
      const newTimeString = newYearString + newMonthString + newDayString;
      console.log("减三个月后的日期:", newTimeString);
      return newTimeString;
