export const categoryes = {
    food: {
      icon: 'shiwu',
      name: '餐饮',
    },
    life: {
      icon: 'shenghuojiaofei',
      name: '生活缴费',
    },
    shopping: {
      icon: 'gouwudai',
      name: '购物',
    },
    transport: {
      icon: 'jiaotongfei',
      name: '交通',
    },
    other: {
      icon: 'ico_e_advice',
      name: '其他',
    },
  }

  // your-amount-to-array-file.js

export function amountToArray(amount) {
  // 将数字转化为字符串以处理小数点
  const amountStr = amount.toString();
  
  // 使用正则表达式来匹配整数和小数部分
  const match = amountStr.match(/^(-?\d+)\.(\d+)$/);
  if (match) {
    // 如果匹配成功，将整数和小数部分存入数组并返回
    const integerPart = match[1];
    const decimalPart = match[2];
    return [integerPart, decimalPart];
  } else {
    // 如果匹配失败，返回默认值
    return [null, null];
  }
}
